// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/// @title BaseFund v5
/// @dev Crowdfunding with ERC20 contributions, custom errors, roles and guards.
contract BaseFundV5 is Ownable, AccessControl, ReentrancyGuard {
  using SafeERC20 for IERC20;

  // Roles
  bytes32 public constant TREASURER_ROLE = keccak256("TREASURER_ROLE");

  // Token used for contributions
  IERC20 public immutable contributionToken;

  // Accounting
  mapping(address => uint256) public contributions;
  uint256 public totalContributions;

  // Events
  event ContributionReceived(address indexed contributor, uint256 amount);
  event RefundIssued(address indexed contributor, uint256 amount);
  event WithdrawExecuted(address indexed to, uint256 amount);

  // Errors
  error ZeroAmount();
  error ZeroAddress();
  error InsufficientContributorBalance(uint256 available, uint256 requested);
  error InsufficientContractBalance(uint256 available, uint256 requested);

  /// @param token ERC20 used to contribute
  /// NOTE: OZ v5 Ownable requires initialOwner -> pass msg.sender
  constructor(IERC20 token) Ownable(msg.sender) {
    if (address(token) == address(0)) revert ZeroAddress();
    contributionToken = token;

    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    _grantRole(TREASURER_ROLE, msg.sender);
  }

  /// @notice Contribute `amount` tokens (must approve first)
  function contribute(uint256 amount) external nonReentrant {
    if (amount == 0) revert ZeroAmount();

    contributionToken.safeTransferFrom(msg.sender, address(this), amount);

    contributions[msg.sender] += amount;
    totalContributions += amount;

    emit ContributionReceived(msg.sender, amount);
  }

  /// @notice Refund up to your contributed tokens
  function refund(uint256 amount) external nonReentrant {
    if (amount == 0) revert ZeroAmount();

    uint256 userBal = contributions[msg.sender];
    if (userBal < amount) revert InsufficientContributorBalance(userBal, amount);

    uint256 contractBal = contributionToken.balanceOf(address(this));
    if (contractBal < amount) revert InsufficientContractBalance(contractBal, amount);

    unchecked {
      contributions[msg.sender] = userBal - amount;
      totalContributions -= amount;
    }

    contributionToken.safeTransfer(msg.sender, amount);
    emit RefundIssued(msg.sender, amount);
  }

  /// @notice Treasurer withdraws tokens to `to`
  function withdraw(uint256 amount, address to)
    external
    nonReentrant
    onlyRole(TREASURER_ROLE)
  {
    if (amount == 0) revert ZeroAmount();
    if (to == address(0)) revert ZeroAddress();

    uint256 contractBal = contributionToken.balanceOf(address(this));
    if (contractBal < amount) revert InsufficientContractBalance(contractBal, amount);

    contributionToken.safeTransfer(to, amount);
    emit WithdrawExecuted(to, amount);
  }

  /// @dev Owner admin helpers (role management)
  function grantTreasurer(address account) external onlyOwner {
    _grantRole(TREASURER_ROLE, account);
  }

  function revokeTreasurer(address account) external onlyOwner {
    _revokeRole(TREASURER_ROLE, account);
  }
}

