// SPDX-License-Identifier: MIT
pragma solidity 0.8.24;

/**
 * BaseFundV10
 * - Crowdfunding ERC20 (start/end, softCap/hardCap)
 * - Seguridad: SafeERC20, ReentrancyGuard, Pausable
 * - Gobernanza: Ownable2Step + AccessControl (TREASURER/PAUSER/AUDITOR)
 * - Allowlist opcional (Merkle)
 * - EIP-2612: contributeWithPermit(...)
 * - EIP-2771: meta-transactions (trusted forwarder)
 * - Límite por wallet (maxPerWallet) y cooldown entre retiros (withdrawCooldown)
 */

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Permit} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import {MerkleProof} from "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Ownable2Step} from "@openzeppelin/contracts/access/Ownable2Step.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

import {Context} from "@openzeppelin/contracts/utils/Context.sol";
import {ERC2771Context} from "@openzeppelin/contracts/metatx/ERC2771Context.sol";

contract BaseFundV10 is
    ERC2771Context,
    Ownable2Step,
    AccessControl,
    ReentrancyGuard,
    Pausable
{
    using SafeERC20 for IERC20;

    // ---- Roles ----
    bytes32 public constant TREASURER_ROLE = keccak256("TREASURER_ROLE");
    bytes32 public constant PAUSER_ROLE    = keccak256("PAUSER_ROLE");
    bytes32 public constant AUDITOR_ROLE   = keccak256("AUDITOR_ROLE");

    // ---- Errors ----
    error ZeroAddress();
    error ZeroAmount();
    error CampaignNotStarted();
    error CampaignEnded();
    error CapExceeded();
    error RefundNotAllowed();
    error NothingToRefund();
    error SoftCapNotReached();
    error SoftCapReached();
    error TreasuryNotSet();
    error AllowlistRequired();
    error InvalidProof();
    error MaxPerWalletExceeded();
    error CooldownActive();

    // ---- Storage ----
    IERC20  public immutable token;
    address public treasury;
    uint64  public start;
    uint64  public end;
    uint256 public softCap;
    uint256 public hardCap;

    uint256 public totalContributed;
    mapping(address => uint256) public contributed;

    // Allowlist (opcional)
    bytes32 public allowlistRoot;   // 0x0 => sin uso
    bool    public allowlistEnabled;

    // Límites y cooldown
    uint256 public maxPerWallet;        // 0 => sin límite
    uint64  public withdrawCooldown;    // segundos; 0 => sin cooldown
    uint64  public lastWithdrawAt;      // timestamp último withdraw

    // ---- Events ----
    event Contributed(address indexed sender, uint256 amount, uint256 totalAfter);
    event ContributedAllowlisted(address indexed sender, uint256 amount, uint256 totalAfter);
    event Refunded(address indexed sender, uint256 amount);
    event Withdrawn(address indexed to, uint256 amount);
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    event WindowUpdated(uint64 oldStart, uint64 oldEnd, uint64 newStart, uint64 newEnd);
    event AllowlistRootSet(bytes32 oldRoot, bytes32 newRoot);
    event AllowlistToggled(bool enabled);
    event MaxPerWalletSet(uint256 oldMax, uint256 newMax);
    event WithdrawCooldownSet(uint64 oldCd, uint64 newCd);

    constructor(
        address initialOwner,
        address trustedForwarder,   // EIP-2771; 0x000...000 si no lo usás aún
        address token_,
        address treasury_,
        uint64  start_,
        uint64  end_,
        uint256 softCap_,
        uint256 hardCap_,
        uint256 maxPerWallet_,      // unidades del token; 0 = sin límite
        uint64  withdrawCooldown_,  // segundos; 0 = sin cooldown
        bytes32 allowlistRoot_,     // 0x0 si no usás allowlist
        bool    allowlistEnabled_   // false si no usás allowlist
    )
        ERC2771Context(trustedForwarder)
        Ownable(initialOwner)
    {
        if (token_ == address(0)) revert ZeroAddress();
        if (treasury_ == address(0)) revert ZeroAddress();
        if (start_ >= end_) revert();

        token = IERC20(token_);
        treasury = treasury_;
        start = start_;
        end = end_;
        softCap = softCap_;
        hardCap = hardCap_;
        maxPerWallet = maxPerWallet_;
        withdrawCooldown = withdrawCooldown_;
        allowlistRoot = allowlistRoot_;
        allowlistEnabled = allowlistEnabled_;

        _grantRole(DEFAULT_ADMIN_ROLE, initialOwner);
        _grantRole(TREASURER_ROLE, initialOwner);
        _grantRole(PAUSER_ROLE, initialOwner);
        _grantRole(AUDITOR_ROLE, initialOwner);
    }

    // ---- Views ----
    function campaignActive() public view returns (bool) {
        return block.timestamp >= start && block.timestamp < end && !paused();
    }
    function campaignSuccessful() public view returns (bool) {
        return totalContributed >= softCap;
    }

    // ---- Admin/Owner ----
    function setTreasury(address newTreasury) external onlyOwner {
        if (newTreasury == address(0)) revert ZeroAddress();
        emit TreasuryUpdated(treasury, newTreasury);
        treasury = newTreasury;
    }
    function updateWindow(uint64 newStart, uint64 newEnd) external onlyOwner {
        if (block.timestamp >= start) revert CampaignNotStarted(); // sólo pre-start
        if (newStart >= newEnd) revert();
        emit WindowUpdated(start, end, newStart, newEnd);
        start = newStart;
        end   = newEnd;
    }
    function setAllowlistRoot(bytes32 newRoot) external onlyOwner {
        emit AllowlistRootSet(allowlistRoot, newRoot);
        allowlistRoot = newRoot;
    }
    function setAllowlistEnabled(bool enabled) external onlyOwner {
        allowlistEnabled = enabled;
        emit AllowlistToggled(enabled);
    }
    function setMaxPerWallet(uint256 newMax) external onlyOwner {
        emit MaxPerWalletSet(maxPerWallet, newMax);
        maxPerWallet = newMax;
    }
    function setWithdrawCooldown(uint64 newCd) external onlyOwner {
        emit WithdrawCooldownSet(withdrawCooldown, newCd);
        withdrawCooldown = newCd;
    }

    // Pausable
    function pause()   external onlyRole(PAUSER_ROLE) { _pause(); }
    function unpause() external onlyRole(PAUSER_ROLE) { _unpause(); }

    // ---- Contribute (sin allowlist) ----
    function contribute(uint256 amount) external nonReentrant whenNotPaused {
        if (allowlistEnabled) revert AllowlistRequired();
        _contribute(_msgSender(), amount, true);
        emit Contributed(_msgSender(), amount, totalContributed);
    }

    // ---- Contribute (allowlist) ----
    function contributeAllowlisted(uint256 amount, bytes32[] calldata proof)
        external nonReentrant whenNotPaused
    {
        if (!allowlistEnabled) revert AllowlistRequired();
        bytes32 leaf = keccak256(abi.encodePacked(_msgSender()));
        if (!MerkleProof.verify(proof, allowlistRoot, leaf)) revert InvalidProof();
        _contribute(_msgSender(), amount, true);
        emit ContributedAllowlisted(_msgSender(), amount, totalContributed);
    }

    // ---- Contribute con PERMIT ----
    function contributeWithPermit(
        uint256 amount,
        uint256 deadline,
        uint8 v, bytes32 r, bytes32 s
    ) external nonReentrant whenNotPaused {
        if (allowlistEnabled) revert AllowlistRequired();
        IERC20Permit(address(token)).permit(_msgSender(), address(this), amount, deadline, v, r, s);
        _contribute(_msgSender(), amount, false);
        emit Contributed(_msgSender(), amount, totalContributed);
    }
    function contributeAllowlistedWithPermit(
        uint256 amount,
        bytes32[] calldata proof,
        uint256 deadline,
        uint8 v, bytes32 r, bytes32 s
    ) external nonReentrant whenNotPaused {
        if (!allowlistEnabled) revert AllowlistRequired();
        bytes32 leaf = keccak256(abi.encodePacked(_msgSender()));
        if (!MerkleProof.verify(proof, allowlistRoot, leaf)) revert InvalidProof();
        IERC20Permit(address(token)).permit(_msgSender(), address(this), amount, deadline, v, r, s);
        _contribute(_msgSender(), amount, false);
        emit ContributedAllowlisted(_msgSender(), amount, totalContributed);
    }

    // ---- Refund / Withdraw ----
    function refund(uint256 amount) external nonReentrant {
        if (block.timestamp < end) revert RefundNotAllowed();
        if (campaignSuccessful()) revert SoftCapReached();
        if (amount == 0) revert ZeroAmount();

        address sender = _msgSender();
        uint256 deposited = contributed[sender];
        if (deposited == 0) revert NothingToRefund();
        if (amount > deposited) amount = deposited;

        contributed[sender] = deposited - amount;
        totalContributed -= amount;

        token.safeTransfer(sender, amount);
        emit Refunded(sender, amount);
    }

    function withdraw(uint256 amount) external nonReentrant onlyRole(TREASURER_ROLE) {
        if (treasury == address(0)) revert TreasuryNotSet();
        if (block.timestamp < end) revert RefundNotAllowed();
        if (!campaignSuccessful()) revert SoftCapNotReached();
        if (amount == 0) revert ZeroAmount();
        if (withdrawCooldown != 0 && block.timestamp < lastWithdrawAt + withdrawCooldown) {
            revert CooldownActive();
        }

        lastWithdrawAt = uint64(block.timestamp);
        token.safeTransfer(treasury, amount);
        emit Withdrawn(treasury, amount);
    }

    // ---- Internals ----
    function _contribute(address sender, uint256 amount, bool checkAllowance) internal {
        if (amount == 0) revert ZeroAmount();
        if (block.timestamp < start) revert CampaignNotStarted();
        if (block.timestamp >= end) revert CampaignEnded();

        if (maxPerWallet != 0 && contributed[sender] + amount > maxPerWallet) {
            revert MaxPerWalletExceeded();
        }

        uint256 newTotal = totalContributed + amount;
        if (newTotal > hardCap) revert CapExceeded();

        contributed[sender] += amount;
        totalContributed = newTotal;

        if (checkAllowance) {
            require(IERC20(address(token)).allowance(sender, address(this)) >= amount, "allowance");
        }
        token.safeTransferFrom(sender, address(this), amount);
    }

    // ---- Overrides de Context / ERC2771 ----
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(AccessControl)
        returns (bool)
    { return super.supportsInterface(interfaceId); }

    function _msgSender()
        internal
        view
        override(Context, ERC2771Context)
        returns (address)
    { return ERC2771Context._msgSender(); }

    function _msgData()
        internal
        view
        override(Context, ERC2771Context)
        returns (bytes calldata)
    { return ERC2771Context._msgData(); }

    function _contextSuffixLength()
        internal
        view
        override(Context, ERC2771Context)
        returns (uint256)
    { return ERC2771Context._contextSuffixLength(); }

    // ---- Fallbacks ----
    receive() external payable { revert(); }
    fallback() external payable { revert(); }
}

